mapper.select:
    select drug_id, serial_num, generic_name, reagent_type, drug_size, company, transver_num,
    factory_name, product_name, tender_price, approval_num, approval_num_date, import_produc_status,
    package_material, drug_pack_comp, lr_price, retail_price_from, quality_level, quality_level_spec,
    test_report_status, drug_report_id, drug_report_id_date, drug_specification, drug_tranl_status,
    drug_type, drug_examine_status
    from drug_info


        and serial_num = #{druginfoVO.serialNum}


        and generic_name = #{druginfoVO.genericName}


        and reagent_type = #{druginfoVO.reagentType}


        and drug_size = #{druginfoVO.drugSize}


        and company = #{druginfoVO.company}


        and transver_num = #{druginfoVO.transverNum}


        and factory_name = #{druginfoVO.factoryName}


        and product_name = #{druginfoVO.productName}


        and  lr_price BETWEEN #{druginfoVO.max} and #{druginfoVO.min}


        and quality_level = #{druginfoVO.qualityLevel}


        and drug_type = #{druginfoVO.drugType}


        and drug_tranl_status = #{druginfoVO.drugTranlStatus}


        and drug_examine_status = #{druginfoVO.drugExamineStatus}


	group by drug_id
